import Auth from "../../components/auth";
import {Form, Container,Button} from 'react-bootstrap';

import quizQuestions from '../../api/quizQuestions'
import firebase from '../../components/firebase';

function setDataToFireStore() {
    let data = {
        name: 'Los Angeles',
        state: 'CA',
        country: 'USA'
      };


    quizQuestions.forEach((question) => {
        firebase.firestore().collection('questions').doc().set(question);
    })
}

function Create() {



    return(
        <Auth>
            <Container>
                <h1>Create Question Page</h1>
                <Form>
                    <Form.Control className="my-2" placeholder="Question title" />
                    <Form.Control className="my-2" placeholder="Correct answer" />
                    <Form.Control className="my-2" placeholder="Other answer 1" />
                    <Form.Control className="my-2" placeholder="Other answer 2" />
                    <Form.Control className="my-2" placeholder="Other answer 3" />
                </Form>
                <Button onClick={setDataToFireStore} className="my-2">Submit</Button> <br />
            </Container>
        </Auth>
    )
}

export default Create
