import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth';
import firebase from '../components/firebase';
import fb from 'firebase'
import { Component } from 'react';
import Link from 'next/link';

const admin = require("firebase-admin");

// Configure FirebaseUI.
const uiConfig = {
    // Popup signin flow rather than redirect flow.
    signInFlow: 'popup',
    // Redirect to /signedIn after sign in is successful. Alternatively you can provide a callbacks.signInSuccess function.
    signInSuccessUrl: '/signedIn',
    // We will display Google and Facebook as auth providers.
    signInOptions: [
      fb.auth.EmailAuthProvider.PROVIDER_ID
    ],
    callbacks: {
      // Avoid redirects after sign-in.
      signInSuccessWithAuthResult: () => false
    }
  };

class Auth extends Component {
  
  constructor(props) {
    super(props);
    this.state =  {
      isSignedIn: false // Local signed-in state.
    };
  }

    // Listen to the Firebase Auth state and set the local state.
  componentDidMount() {
    this.unregisterAuthObserver = firebase.auth().onAuthStateChanged(
      (user) => this.setState({isSignedIn: !!user})
    );


    exports.blockSignup = functions.auth.user().onCreate(event => {
      return admin.auth().updateUser(event.data.uid, { disabled: true })
      .then(userRecord => console.log("Auto blocked user", userRecord.toJSON()))
      .catch(error => console.log("Error auto blocking:", error));
    });
  }
    
    // Make sure we un-register Firebase observers when the component unmounts.
  componentWillUnmount() {
    this.unregisterAuthObserver();
  }
  render() {
    if (!this.state.isSignedIn) {
      return (
        <div>
          <h1>My App</h1>
          <p>Please sign-in:</p>
          <StyledFirebaseAuth uiConfig={uiConfig}  firebaseAuth={firebase.auth()}/>
        </div>
      );
    }
    return (
      <div>
        <h1>My App</h1>
        <p>Welcome {firebase.auth().currentUser.email}! You are now signed-in!</p>
        <Link href="/">
          <a>Home</a>
        </Link>
        {this.props.children}
        <a onClick={() => firebase.auth().signOut()}>Sign-out</a>
      </div>
    );
  }
}

export default Auth
