import Auth from "../../components/auth";
import {Form, Container,Button} from 'react-bootstrap';

import quizQuestions from '../../api/quizQuestions'
import firebase from '../../components/firebase';
import { useState } from "react";


function Create() {
    
    const [question, setQuestion]  = useState({
        title: '',
        correctAnswer: '',
        otherAnswer1: '',
        otherAnswer2: '',
        otherAnswer3: ''
    })
    
    const handleChange = e =>
    setQuestion({ ...question, [e.target.name]: e.target.value });
    
    let questionSent = false;

    const handleSubmit = () => {
        firebase.firestore().collection('questions').doc('da').set(question);
        questionSent = true;
    }

    const renderQuestion = () => {
        <div>
            <h1>Create Question Page</h1>
            <Form>
                <Form.Control name="title" onChange={handleChange} className="my-2" placeholder="Question title" />
                <Form.Control name="correctAnswer" onChange={handleChange} className="my-2" placeholder="Correct answer" />
                <Form.Control name="otherAnswer1" onChange={handleChange} className="my-2" placeholder="Other answer 1" />
                <Form.Control name="otherAnswer2" onChange={handleChange} className="my-2" placeholder="Other answer 2" />
                <Form.Control name="otherAnswer3" onChange={handleChange} className="my-2" placeholder="Other answer 3" />
            </Form>
            <Button onClick={handleSubmit} className="my-2">Submit</Button> <br />
        </div>
    }
    
    const renderSumbited = () => {
        <h1>SUBMITED!</h1>
    }

    return(
        <Auth>
            <Container>
                {!questionSent ? renderQuestion : renderSumbited}   
            </Container>
        </Auth>
    )
}

export default Create
